import express from "express";

export const todosRouteur = express.Router();

todosRouteur.get("/api/todos", (req, rep) => {
  rep.send("Recuperer taches");
});

todosRouteur.post("/api/todos", (req, rep) => {
  const newTodo = req.body.content;
  console.log(newTodo);

  rep.send("creer taches");
});

todosRouteur.put("/api/todos/:id", (req, rep) => {
  rep.send("Modifier taches");
});

todosRouteur.delete("/api/todos/:id", (req, rep) => {
  rep.send("supprimer taches");
});
